import { useEffect, useRef } from "react";
import { useTrip } from "@/lib/tripContext";
import { Button } from "@/components/ui/button";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Loader2, ArrowLeft, Plane } from "lucide-react";
import { motion } from "framer-motion";

function getSeenQuestionIds(city: string): string[] {
  try {
    const key = `braintrip_seen_q_${city.toLowerCase().trim()}`;
    const stored = localStorage.getItem(key);
    return stored ? JSON.parse(stored) : [];
  } catch {
    return [];
  }
}

function saveSeenQuestionIds(city: string, questionIds: string[]) {
  try {
    const key = `braintrip_seen_q_${city.toLowerCase().trim()}`;
    const existing = getSeenQuestionIds(city);
    const merged = Array.from(new Set(existing.concat(questionIds))).slice(-200);
    localStorage.setItem(key, JSON.stringify(merged));
  } catch {}
}

function clearSeenQuestionIds(city: string) {
  try {
    const key = `braintrip_seen_q_${city.toLowerCase().trim()}`;
    localStorage.removeItem(key);
  } catch {}
}

export default function LoadingPage() {
  const trip = useTrip();
  const { toast } = useToast();
  const hasFetched = useRef(false);

  useEffect(() => {
    if (hasFetched.current) return;
    if (!trip.city) return;
    hasFetched.current = true;

    const fetchData = async () => {
      try {
        if (trip.mode === "quiz") {
          const excludeQuestionIds = getSeenQuestionIds(trip.city);
          const res = await apiRequest("POST", "/api/quiz/generate", {
            city: trip.city,
            difficulty: trip.difficulty,
            count: 8,
            excludeQuestionIds,
          });
          const data = await res.json();

          if (data.poolExhausted) {
            clearSeenQuestionIds(trip.city);
            toast({
              title: `You've mastered ${data.cityLabel || trip.city}!`,
              description: "Starting a fresh set of questions.",
            });
          }

          if (Array.isArray(data.questionIds) && data.questionIds.length > 0) {
            saveSeenQuestionIds(trip.city, data.questionIds);
          }

          trip.setQuestions(data.questions);
          trip.setCurrentQuestionIndex(0);
          trip.setScore(0);
          trip.setUserAnswers(new Array(data.questions.length).fill(null));
          trip.setScreen("quiz");
        } else {
          const exclude = trip.itinerary.map((item) => item.title);
          const res = await apiRequest("POST", "/api/suggestions/generate", {
            city: trip.city,
            hotelLocation: trip.hotelLocation || undefined,
            exclude,
            excludePlaceIds: [],
          });
          const data = await res.json();
          trip.setSuggestions(data.suggestions);
          trip.setScreen("suggestions");
        }
      } catch (err: any) {
        toast({
          title: "Something went wrong",
          description: err.message || "Please try again",
          variant: "destructive",
        });
        trip.setScreen("home");
      }
    };

    fetchData();
  }, [trip.city]);

  return (
    <div className="min-h-screen bg-background flex flex-col">
      <header className="p-4">
        <Button
          size="icon"
          variant="ghost"
          onClick={() => trip.setScreen("home")}
          data-testid="button-loading-cancel"
        >
          <ArrowLeft className="w-4 h-4" />
        </Button>
      </header>

      <main className="flex-1 flex items-center justify-center p-8">
        <div className="text-center">
          <motion.div
            animate={{
              y: [0, -10, 0],
              rotate: [0, 5, -5, 0],
            }}
            transition={{
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut",
            }}
            className="inline-flex items-center justify-center w-20 h-20 rounded-full bg-primary/10 mb-6"
          >
            <Plane className="w-10 h-10 text-primary" />
          </motion.div>

          <div className="flex items-center justify-center gap-2 mb-3">
            <Loader2 className="w-4 h-4 animate-spin text-primary" />
            <span className="text-sm font-medium text-muted-foreground" data-testid="text-loading-message">
              {trip.loadingMessage || "Getting things ready..."}
            </span>
          </div>

          <motion.div
            className="flex justify-center gap-1"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            {[0, 1, 2].map((i) => (
              <motion.div
                key={i}
                className="w-2 h-2 rounded-full bg-primary/30"
                animate={{ scale: [1, 1.3, 1], opacity: [0.3, 1, 0.3] }}
                transition={{ duration: 1.2, repeat: Infinity, delay: i * 0.2 }}
              />
            ))}
          </motion.div>
        </div>
      </main>
    </div>
  );
}

